#ifndef METAVISION_SDK_CV_EVENT_ACTIVE_BLOB_H
#define METAVISION_SDK_CV_EVENT_ACTIVE_BLOB_H

#include <cstdint>

#include "metavision/sdk/base/utils/timestamp.h"

namespace Metavision {

/// @brief Event describing the position and size of a track generated by an active light source
struct EventActiveTrack {
    enum class Status : std::uint8_t { Tracked, Lost };

    float x;          ///< Blob's center's abscissa in the sensor
    float y;          ///< Blob's center's ordinate in the sensor
    float radius;     ///< Track's influence radius (i.e. radius below which an event is associated to the track)
    timestamp t;      ///< Timestamp of the last update of the center
    std::uint32_t id; ///< ID of the source that generated this track
    Status status;    ///< Blob's status
};

} // namespace Metavision

#endif // METAVISION_SDK_CV_EVENT_ACTIVE_BLOB_H
